function parallax(){if($(".block-container").length>0){var n=$(".block-content"),o=$(".block-container"),t=$(o).offset().top,l=$(window).scrollTop(),a=t-l,c=$(n).offset().top,e=window.innerHeight,r=c-l,i=e-r,f=.15;n.css("transform","translateY("+a*f+"px")}}$(document).ready(function(){$(".block-container").length&&parallax()}),$(window).scroll(function(n){$(".block-container").length&&parallax()});